const appointmentId = $('Webhook').item.json.body.appointmentId;
const googleEventId = $('Webhook').item.json.body.googleEventId;
const nombre = $('Webhook').item.json.body.nombre;
const email = $('Webhook').item.json.body.email;
const hora = $('Webhook').item.json.body.hora;

const events = items.map(item => item.json);

// Paso 1: Buscar por googleEventId exacto
let targetEvent = events.find(e => 
  e.id === googleEventId || 
  e.iCalUID === googleEventId || 
  e.iCalUID === `${googleEventId}@google.com`
);

// Paso 2: Si no lo encuentra por ID, buscar por appointmentId + validaciones adicionales
if (!targetEvent && appointmentId) {
  const candidates = events.filter(e => 
    e.description && e.description.includes(`ID Turno: ${appointmentId}`)
  );

  // Si hay m√∫ltiples candidatos, validar con nombre, email u hora
  if (candidates.length === 1) {
    targetEvent = candidates[0];
  } else if (candidates.length > 1) {
    // Filtrar por nombre en summary
    targetEvent = candidates.find(e => 
      (nombre && e.summary && e.summary.includes(nombre)) ||
      (email && e.description && e.description.includes(email)) ||
      (hora && e.start && e.start.dateTime && e.start.dateTime.includes(hora))
    );

    // Si a√∫n no encuentra, tomar el primero (√∫ltimo recurso)
    if (!targetEvent) {
      targetEvent = candidates[0];
    }
  }
}

if (targetEvent) {
  return [{ json: { 
    eventId: targetEvent.id, 
    found: true,
    summary: targetEvent.summary,
    start: targetEvent.start ? targetEvent.start.dateTime : null
  } }];
} else {
  return [{ json: { 
    eventId: null, 
    found: false, 
    error: 'Evento no encontrado en el calendario',
    searchedId: googleEventId,
    searchedAppointmentId: appointmentId
  } }];
}
